function [y]= solver_AB2(f,t0,tn,y,h)%   ADAMS_BASHFORTH_SOLVER 2 stage Method %   Approximates a solution to a DE %   f = differential equation in a form: %           y(t+h/2) = y(t) + h/2 * y'(t+h/2)%   t0 -> tn =  the interval over which the evaluation occurs %   h = the step size%   y = the initial state of the equation    w0 = y;    t1 = t0+h;    w1 = solver_RK4(f,t0,t1,y,h);                 % Bootstrap the first step           while t1 < tn           y = w1 + 1/2 * h * ( 3*f(t1,w1) - f(t0, w0));              w1 = y;  w0 = w1;                                   % Move up all the weights           t1 = t1+h; t0 = t0+h;                                % Increment all the times    endwhileendfunction